mapper.select:
SELECT , dz.dwmc jbxx_zddwmc, jz.jzmc jbxx_jzmc FROM YAGL_XFSS t
Left Join Yagl_Dx_Zddw dz On dz.uuid = t.zddwid And dz.DELETE_FLAG = 'N'
Left Join Yagl_Dwjzxx jz On jz.jzid = t.jzid And jz.DELETE_FLAG = 'N' WHERE t.DELETE_FLAG = 'N' AND t.xfssid = #{value}

mapper.insert:
INSERT INTO YAGL_XFSS(xfssid,jzid,xfssmc,xfsslx,iszddw,zddwid,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.xfssid,jdbcType=VARCHAR} xfssid,#{item.jzid,jdbcType=VARCHAR} jzid,#{item.xfssmc,jdbcType=VARCHAR} xfssmc,#{item.xfsslx,jdbcType=VARCHAR} xfsslx,#{item.iszddw,jdbcType=VARCHAR} iszddw,#{item.zddwid,jdbcType=VARCHAR} zddwid,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

