mapper.select:
SELECT , c2.code_name jzsyxzmc, c4.code_name jzjgmc FROM YAGL_JXCS_JZXX t
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'JZSYXZ' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.jzsyxz = c2.CODE_VALUE
Left Join SYS_CODELIST c3 on c3.CODETYPE = 'JZJG' And c3.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c4 On c3.CODEID = c4.CODEID And c4.DELETE_FLAG = 'N' and t.jzjg = c4.CODE_VALUE WHERE t.DELETE_FLAG = 'N' AND t.JZID = #{value}

mapper.insert:
INSERT INTO
YAGL_JXCS_JZXX(jzid,jzmc,jzwz,jzsyxz,jzjg,gnms,zdmj,jzmj,dsgd,dxgd,dscs,dxcs,bnc,xqxclx,yjddsc,bz,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,datasource,jdh,sjc,reserve1,reserve2,reserve3,reserve4)
SELECT SYS_GUID() jzid, A.*
FROM (

    SELECT
    #{item.jzid,jdbcType=VARCHAR} jzid,#{item.jzmc,jdbcType=VARCHAR} jzmc,#{item.jzwz,jdbcType=VARCHAR}
    jzwz,#{item.jzsyxz,jdbcType=VARCHAR} jzsyxz,#{item.jzjg,jdbcType=VARCHAR} jzjg,#{item.gnms,jdbcType=VARCHAR}
    gnms,#{item.zdmj,jdbcType=DOUBLE} zdmj,#{item.jzmj,jdbcType=DOUBLE} jzmj,#{item.dsgd,jdbcType=DOUBLE}
    dsgd,#{item.dxgd,jdbcType=DOUBLE} dxgd,#{item.dscs,jdbcType=DOUBLE} dscs,#{item.dxcs,jdbcType=DOUBLE}
    dxcs,#{item.bnc,jdbcType=VARCHAR} bnc,#{item.xqxclx,jdbcType=VARCHAR} xqxclx,#{item.yjddsc,jdbcType=DOUBLE}
    yjddsc,#{item.bz,jdbcType=VARCHAR} bz,'N' delete_flag,#{item.cjrid,jdbcType=VARCHAR}
    cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR}
    xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE}
    xgsj,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.jdh,jdbcType=VARCHAR}
    jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR}
    reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR}
    reserve3,#{item.reserve4,jdbcType=DATE} reserve4
    FROM DUAL

) A

mapper.update:
UPDATE YAGL_JXCS_JZXX
jzid=#{jzid}, jzmc=#{jzmc}, jzwz=#{jzwz}, jzsyxz=#{jzsyxz}, jzjg=#{jzjg}, gnms=#{gnms}, zdmj=#{zdmj}, jzmj=#{jzmj}, dsgd=#{dsgd}, dxgd=#{dxgd}, dscs=#{dscs}, dxcs=#{dxcs}, bnc=#{bnc}, xqxclx=#{xqxclx}, yjddsc=#{yjddsc}, bz=#{bz}, delete_flag='N', cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), datasource=#{datasource}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE JZID = #{jzid}

UPDATE YAGL_JXCS_JZXX
delete_flag=#{deleteFlag}, xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), datasource=#{datasource}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE JZID = #{jzid}
