mapper.insert:
INSERT INTO
CFPA_QY_CPJS(uuid,qyid,cptp,cplx,cpjj,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4)
SELECT SYS_GUID() uuid, A.*
FROM (

    SELECT
    #{item.qyid,jdbcType=VARCHAR} qyid,#{item.cptp,jdbcType=VARCHAR}
    cptp,#{item.cplx,jdbcType=VARCHAR} cplx,#{item.cpjj,jdbcType=VARCHAR} cpjj,'N'
    delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR}
    cjrmc,SYSDATE cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR}
    xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR}
    reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR}
    reserve3,#{item.reserve4,jdbcType=DATE} reserve4
    FROM DUAL

) A

mapper.update:
UPDATE CFPA_QY_CPJS
qyid=#{qyid}, cptp=#{cptp}, cplx=#{cplx}, cpjj=#{cpjj}, delete_flag=#{deleteFlag}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}
