mapper.insert:
INSERT INTO
CFPA_QY_KPXX(uuid,qyid,kplx,kpgsmc,tyshxydm,gsdz,dhhm,khyh,yhzh,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4)
SELECT SYS_GUID() uuid, A.*
FROM (

    SELECT
    #{item.uuid,jdbcType=VARCHAR} uuid,#{item.qyid,jdbcType=VARCHAR} qyid,#{item.kplx,jdbcType=VARCHAR}
    kplx,#{item.kpgsmc,jdbcType=VARCHAR} kpgsmc,#{item.tyshxydm,jdbcType=VARCHAR}
    tyshxydm,#{item.gsdz,jdbcType=VARCHAR} gsdz,#{item.dhhm,jdbcType=VARCHAR} dhhm,#{item.khyh,jdbcType=VARCHAR}
    khyh,#{item.yhzh,jdbcType=VARCHAR} yhzh,'N' delete_flag,#{item.cjrid,jdbcType=VARCHAR}
    cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR}
    xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR}
    reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR}
    reserve3,#{item.reserve4,jdbcType=DATE} reserve4
    FROM DUAL

) A

mapper.update:
UPDATE CFPA_QY_KPXX
qyid=#{qyid}, kplx=#{kplx}, kpgsmc=#{kpgsmc}, tyshxydm=#{tyshxydm}, gsdz=#{gsdz}, dhhm=#{dhhm}, khyh=#{khyh}, yhzh=#{yhzh}, delete_flag='N', cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}
