mapper.select:

SELECT 

FROM SYS_INFOCOLLECT t 
WHERE t.DELETE_FLAG = 'N' AND  t.PKID = #{value}

mapper.insert:
INSERT INTO SYS_INFOCOLLECT(uuid,dwid,unscid,delete_flag,datasource,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.dwid,jdbcType=VARCHAR} dwid,#{item.unscid,jdbcType=VARCHAR} unscid,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE SYS_INFOCOLLECT
uuid=#{uuid}, dwid=#{dwid}, unscid=#{unscid}, delete_flag='N', datasource=#{datasource}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}
