mapper.select:
SELECT

FROM CFPA_QY_ZWYX t
WHERE t.DELETE_FLAG = 'N' AND t.QYID = #{value}

mapper.insert:
INSERT INTO
CFPA_QY_ZWYX(uuid,qyid,bzzwgs,sngdzw,swgdzw,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4)
SELECT SYS_GUID() uuid, A.*
FROM (

    SELECT
    #{item.uuid,jdbcType=VARCHAR} uuid,#{item.qyid,jdbcType=VARCHAR} qyid,#{item.bzzwgs,jdbcType=DOUBLE}
    bzzwgs,#{item.sngdzw,jdbcType=DOUBLE} sngdzw,#{item.swgdzw,jdbcType=DOUBLE} swgdzw,'N'
    delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR}
    cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR}
    xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR}
    reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR}
    reserve3,#{item.reserve4,jdbcType=DATE} reserve4
    FROM DUAL

) A

mapper.update:
UPDATE CFPA_QY_ZWYX
uuid=#{uuid}, qyid=#{qyid}, bzzwgs=#{bzzwgs}, bzzwgs=NULL , sngdzw=#{sngdzw}, sngdzw=NULL , swgdzw=#{swgdzw}, swgdzw=NULL , delete_flag='N', cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}

UPDATE CFPA_QY_ZWYX
delete_flag='Y', xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate
WHERE QYID = #{qyid}

mapper.select:
SELECT TT1.QYID, TT1.Zwgsmc, TT1.Ywgsmc, TT1.Lxr, TT1.Lxrsj, TT1.CPLX, NVL(TT2.BZZWGS,0) BZZWGS, NVL(TT2.SNGDZW,0) SNGDZW, NVL(TT2.SWGDZW,0) SWGDZW FROM ( SELECT DISTINCT T1.QYID, T1.Zwgsmc, T1.Ywgsmc, T1.Lxr, T1.Lxrsj, CONCAT(SUBSTR(T2.CPLX, 0, 1),'000') CPLX FROM CFPA_QY_JBXX T1 INNER JOIN CFPA_QY_CPJS T2 ON T1.QYID = T2.QYID AND T2.DELETE_FLAG = 'N' INNER JOIN SYS_CODELIST_DETAIL T3 ON T2.Cplx = T3.CODE_VALUE AND T3.CODETYPE = 'CPLX' AND T3.DELETE_FLAG = 'N' WHERE T1.DELETE_FLAG = 'N') TT1 LEFT JOIN CFPA_QY_ZWYX TT2 ON TT1.QYID = TT2.QYID AND TT2.DELETE_FLAG = 'N'
where TT1.CPLX =#{cplx}
order by TT1.Zwgsmc desc
