mapper.insert:
INSERT INTO CFPA_ZW_JBXX(uuid,zwh,zwlb,cklx,zwmj,zwzt,qyid,zwkd,zwcd,zwmc,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4,zgid,bhzh,bhzt,bhzc,bhzs,mczt,mczh,mczc,mczs) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.zwh,jdbcType=VARCHAR} zwh,#{item.zwlb,jdbcType=VARCHAR} zwlb,#{item.cklx,jdbcType=VARCHAR} cklx,#{item.zwmj,jdbcType=DOUBLE} zwmj,#{item.zwzt,jdbcType=VARCHAR} zwzt,#{item.qyid,jdbcType=VARCHAR} qyid,#{item.zwkd,jdbcType=DOUBLE} zwkd,#{item.zwcd,jdbcType=DOUBLE} zwcd,#{item.zwmc,jdbcType=VARCHAR} zwmc,'N' delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4,#{item.zgid,jdbcType=VARCHAR} zgid,#{item.bhzh,jdbcType=VARCHAR} bhzh,#{item.bhzt,jdbcType=VARCHAR} bhzt,#{item.bhzc,jdbcType=VARCHAR} bhzc,#{item.bhzs,jdbcType=VARCHAR} bhzs,#{item.mczt,jdbcType=VARCHAR} mczt,#{item.mczh,jdbcType=VARCHAR} mczh,#{item.mczc,jdbcType=VARCHAR} mczc,#{item.mczs,jdbcType=VARCHAR} mczs FROM DUAL
) A

mapper.update:
UPDATE CFPA_ZW_JBXX
xgsj=sysdate, uuid=#{uuid}, zwh=#{zwh}, zwlb=#{zwlb}, cklx=#{cklx}, zwmj=#{zwmj}, zwzt=#{zwzt}, qyid=#{qyid}, zwkd=#{zwkd}, zwcd=#{zwcd}, zwmc=#{zwmc}, delete_flag='N', delete_flag=#{deleteFlag}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss'), zgid=#{zgid}, bhzh=#{bhzh}, bhzt=#{bhzt}, bhzc=#{bhzc}, bhzs=#{bhzs}, mczt=#{mczt}, mczh=#{mczh}, mczc=#{mczc}, mczs=#{mczs}
WHERE uuid = #{uuid} reserve1=#{reserve1}

mapper.select:
WITH TEMP_ZW AS( SELECT QYID, ZWH FROM CFPA_ZW_JBXX WHERE DELETE_FLAG = 'N' AND QYID IS NOT NULL
AND SUBSTR(ZWH,1,2) IN
#{item}
) SELECT A.QYID, A.GSMC, A.LXR, A.LXRSJ, A.ZWNUM, B.ZWH FROM ( SELECT T1.QYID, NVL(T2.ZWGSMC,T2.YWGSMC) GSMC, T2.LXR, T2.LXRSJ, COUNT(1) ZWNUM FROM TEMP_ZW T1 INNER JOIN CFPA_QY_JBXX T2 ON T1.QYID = T2.QYID AND T2.DELETE_FLAG = 'N' GROUP BY T1.QYID, NVL(T2.ZWGSMC,T2.YWGSMC), T2.LXR, T2.LXRSJ ) A INNER JOIN ( SELECT QYID, MAX(ZWH) ZWH FROM ( SELECT QYID, TO_CHAR(WMSYS.WM_CONCAT(ZWH) OVER(PARTITION BY QYID ORDER BY SUBSTR(zwh,1,4) ASC, TO_NUMBER(TRANSLATE (REPLACE(zwh,'-',''),'0123456789'||REPLACE(zwh,'-',''),'0123456789')) ASC)) ZWH FROM TEMP_ZW ) GROUP BY QYID ) B ON A.QYID = B.QYID ORDER BY ZWNUM DESC ,ZWH

SELECT t.uuid uuid, t.zwh zwh, t.zwmj zwmj, round(t1.fljg *t.zwmj /t1.flmj,4) as zwjg, round(t1.fljg_eng * t.zwmj/t1.flmj,4) as zwjg_eng FROM CFPA_ZW_JBXX t INNER JOIN CFPA_ZW_FLXX t1 ON t1.DELETE_FLAG = 'N' AND t1.ZWFL = '1' WHERE t.DELETE_FLAG = 'N' and t.zwlb = '标准展位'
and t.qyid = #{qyid}
ORDER BY substr(t.zwh,1,4) asc, to_number(TRANSLATE (replace(t.zwh,'-',''),'0123456789'||replace(t.zwh,'-',''),'0123456789')) asc

SELECT t1.uuid uuid , t1.zwh zwh , t1.zwmj zwmj, c2.code_name cklx, round(t2.fljg*t1.zwmj/t2.flmj,4) as zwjg, round(t2.fljg_eng*t1.zwmj/t2.flmj, 4) as zwjg_eng FROM ( SELECT t.uuid uuid, t.zwh zwh, t.zwmj zwmj, case when t.cklx = '一面开' then '1' when t.cklx = '两面开' then '2' when t.cklx = '三面开' then '3' when t.cklx = '全开' then '4' end as cklx FROM CFPA_ZW_JBXX t WHERE t.DELETE_FLAG = 'N' and t.zwlb = '光地' AND substr(t.zwh,0,2) != 'OD'
and t.qyid = #{qyid}
)t1 inner join CFPA_ZW_FLXX t2 ON t2.DELETE_FLAG = 'N' AND t2.ZWFL = '2' AND t1.cklx = t2.kkfl Left Join SYS_CODELIST c1 on c1.CODETYPE = 'CKLX' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c2.CODEID = c1.CODEID And c2.DELETE_FLAG = 'N' and t1.cklx = c2.CODE_VALUE ORDER BY substr(t1.zwh,1,4) asc, to_number(TRANSLATE (replace(t1.zwh,'-',''),'0123456789'||replace(t1.zwh,'-',''),'0123456789')) asc

SELECT t.uuid uuid, t.zwh zwh, t.zwmj zwmj, t.cklx cklx, round(t1.fljg*t.zwmj/t1.flmj,4) as zwjg, round(t1.fljg_eng*t.zwmj/t1.flmj,4) as zwjg_eng FROM CFPA_ZW_JBXX t INNER JOIN CFPA_ZW_FLXX t1 ON t1.DELETE_FLAG = 'N' AND t1.ZWFL = '3' WHERE t.DELETE_FLAG = 'N' and substr(t.zwh,0,2) = 'OD'
and t.qyid = #{qyid}
ORDER BY substr(t.zwh,1,4) asc, to_number(TRANSLATE (replace(t.zwh,'-',''),'0123456789'||replace(t.zwh,'-',''),'0123456789')) asc
