mapper.select:

SELECT 

FROM CFPA_ZW_MK t 
WHERE t.DELETE_FLAG = 'N' AND  t.uuid = #{value}

mapper.insert:
INSERT INTO CFPA_ZW_MK(stageuuid,shapeuuid,jsondata,uuid,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.stageuuid,jdbcType=VARCHAR} stageuuid,#{item.shapeuuid,jdbcType=VARCHAR} shapeuuid,#{item.jsondata,jdbcType=VARCHAR} jsondata,#{item.uuid,jdbcType=VARCHAR} uuid,'N' delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE CFPA_ZW_MK
stageuuid=#{stageuuid}, shapeuuid=#{shapeuuid}, jsondata=#{jsondata}, uuid=#{uuid}, delete_flag='N', cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), xgsj= sysdate, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE uuid = #{uuid}
